import React from "react";
import { useState } from "react";
function Form() {
    const [url, setUrl] = useState("");

    function updateUrl(formData) {
        setUrl(formData.get(url));
    }

    return (
        <form className="text-base h-8" action={updateUrl}>
            <label className="text-lg h-8">
                Url:
                <input
                    type="text"
                    name="url"
                    className="h-5 border-2 mx-2 border-black h-8 w-3/6 rounded-lg"
                />
            </label>
            <input type="submit" value="Submit" className="bg-slate-300 p-1 rounded-lg" />
        </form >
    );
}

export default Form
