import React from "react";

class Form extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            value: ""
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        console.log(event)
        this.setState({ value: event.target[0].value });
        event.preventDefault();
    }

    render() {
        return (
            <form className="text-base h-8" onSubmit={this.handleSubmit}>
                <label className="text-lg h-8">
                    Url:
                    <input
                        type="text"
                        name="url"
                        value = {this.state.value}
                        className="h-5 border-2 mx-2 border-black h-8 w-3/6 rounded-lg"
                    />
                </label>
                <input type="submit" value="Submit" className="bg-slate-300 p-1 rounded-lg" />
            </form >
        );
    }
}

export default Form
